import './singlereport.css';

const SingleReport = ({ setEd, setLat, setLong, setDesc, setIsOpen, report, setImg }) => {
    const { id, latitude, longitude, description, location, status, title, media_attachments } = report;

    const handlePushImages = () => {
        setImg(media_attachments.uploaded_urls)
    }

    const handleDelete = () => {
        fetch(`/incidents/${id}`, {
            method: 'DELETE',
            headers: {
                'Content-Type': 'application/json'
            }
        })
        .then((response) => {
            if(!response.ok) {
                throw new Error(`Failed to delete request, ${response.statusText}`)
            }
            console.log(response)
        })
        .catch((error) => {
            console.error(error)
        });
    };

    return (
        <li className='main__reportdiv'>
            <div
                id='descp'
                className='descr'>
                Report #{id}
                <div className='buttdiv'>
                    <button onClick={() => {
                        handlePushImages()
                        setEd(id)
                        setLat(latitude)
                        setLong(longitude)
                        setDesc(description)
                        setIsOpen(true)
                    }} id='divbutt'>
                        <span role='img' aria-label='edit'>
                            ‚úèÔ∏è
                        </span>
                    </button>
                    <p className='explainer'>Edit</p>
                    <button onClick={handleDelete} id='divbutt2'>
                        <span role="img" aria-label="delete">
                            üóë
                        </span>
                    </button>
                    <p className='explainer2'>Delete</p>
                </div>
            </div>
        </li>
    )
}

export default SingleReport;
