import './myreports.css';
import { useEffect, useState } from 'react';
import { jwtDecode } from 'jwt-decode';
import SingleReport from '../SingleReport/singlereport';
import Modal from '../Modal/modal';

const MyReports = ({ setIsAuthenticated }) => {

    const [displayReports, setDisplayReports] = useState([])
    const [userName, setUserName] = useState("")
    const [isOpen, setIsOpen] = useState(false)
    const [lat, setLat] = useState("");
    const [long, setLong] = useState("");
    const [desc, setDesc] = useState("");
    const [img, setImg] = useState([]);
    const [ed, setEd] = useState("")



    useEffect(() => {
        const token = localStorage.getItem('access_token')
        const pay_load = jwtDecode(token)
        const current_id = pay_load.userid

        if (token) {
            const payload = JSON.parse(atob(token.split('.')[1]));

            const name = payload.username;
            setUserName(name)
        }

        fetch('/incidents')
            .then((response) => {
                if (!response.ok) {
                    throw new Error("Couldn't get reports");
                }
                return response.json();
            })
            .then((data) => {
                console.log(data)
                setDisplayReports(data);
                console.log(data)
            })
            .catch((error) => { console.error("Error fetching reports", error.message) });
    }, []);

    return (
        <div className='maindiv__myreports'>
            {<Modal setIsOpen={setIsOpen} isOpen={isOpen} latitude={lat} longitude={long} description={desc} id={ed} img={img} />}
            <div>
                <div className='textdiv'>
                    <h4>Welcome Back,</h4>
                    <h2 style={{ textTransform: "capitalize" }}>{userName}</h2>
                </div>
                <div className='logoutbtn'>
                    <h2>My Reports</h2>
                </div>
            </div>
            <div className='uldiv'>
                <ul className='ul'>
                    {dis && displayReports.map((report) => (
                        < SingleReport
                            key = { report.id }
                            setEd = { setEd }
                            setLat = { setLat }
                            setLong = { setLong }
                            setDesc = { setDesc }
                            setIsOpen = { setIsOpen }
                            setImg = { setImg }
                            report = { report }
                        />
                ))}
                </ul>
            </div>
        </div>
    )
}

export default MyReports;
